//Pick a random word from the word list
function pickWord() {
    //select a random number between 0 and the length of Word List
    var randomNumber = Math.floor(Math.random() * wordList.length);
    //set currentWord to that position in wordList
    currentWord = wordList[randomNumber];
    console.log(currentWord);
    setWord(currentWord);
}
//create the underscores for the word
function setWord(currentWord) {
    secretWordDiv.text = "";
    secretWordSpans = "";
    var length = currentWord.length;
    for (var i = 0; i < length; i++) {
        var openSpan = "<span id ='letter" + i + "'>"
        var closeSpan = "</span>";
        secretWordSpans = secretWordSpans + openSpan + "_" + closeSpan;
        secretWordDiv.html(secretWordSpans);
    }
    letterCount.html(" (" + length + " letters)");
}
//Clear the secret word, pick a new word
function reset() {
    remainingGuesses = 9;
    victory = false;
    previousGuesses = [];
    previousGuessText.text(previousGuesses);
    winText.html(wins);
    guessesRemaining.html(remainingGuesses);
    pickWord();
    console.log("reset");
    return;
}

//Show the correctly guessed letter in the secretWord
function reveal(letter) {
    //find where the letter occurs in the current word
    //step through the letters of the current word
    for (var i = 0; i < currentWord.length; i++) {
        console.log(letter);
        if (currentWord[i] == letter) {
            var letterSlot = "letter" + i;
            var span = $("#"+letterSlot);
            span.html(letter);
        }
    }
}

//check if the pressed key is actually a letter
function validInput(letter){
    console.log(letter);  
    console.log("index of: "+letters.indexOf(letter)); 
    if (letters.indexOf(letter) > -1){
        console.log("in true");
        return true;
    }
    else{
        return false;
    }
}
